                        <div className='mb-5 d-flex '>
                            <div>
                                <h1>CRM Leads</h1>
                                <MonthYearCalendar />
                                {/* <input style={{ border: "1px solid #d9d9d9", borderRadius: "4px", padding: "2px 4px", color: "#5F498A", marginTop: '30px' }} type="date" /> */}
                            </div>
                            <div>
                                <CustomLegend colors={Colors} />
                            </div>
                        </div>
